import { Box, GridForm, Markdown, Text } from '@codecademy/gamut';
import { delay } from 'lodash';
import React, { useMemo, useRef, useState } from 'react';
import { useMountedState } from 'react-use';
import request from 'superagent';
import { v4 as uuid } from 'uuid';

import { OauthButtonGroup } from 'components/Forms/OauthButtonGroup';
import { RecaptchaWrapper } from 'components/Forms/RecaptchaWrapper';
import { cfdata } from 'libs/cfdata';
// import { pushDataLayerEvent, trackUserClick } from '~/libs/eventTracking';
// import { logger } from '~/libs/logging/logger';
import { passwordTips } from 'libs/passwordTips';
// import { csrf } from '~/libs/superagent-auth';
// import { safeRedirectUrl } from '~/libs/url';
import { registerPath } from 'libs/urlHelpers';
// import registrationStrings from '~/localized/registration.json';
// import { useFeatureFlag } from 'state/features/hooks';

import { showModal, hideModal, hideCurrentModal, selectModal } from 'state/modals/modalSlice';
import { useAppDispatch, useAppSelector } from 'store/hooks';
import { redirectAfterLogin, serializeRegistrationValues } from './helpers';
import { RegistrationSSOForm } from './RegistrationSSOForm';
import styles from './styles/index.module.scss';
import {
  RegistrationFormProps,
  SerializedValues,
  SubmitValues,
  UserSubmitKey,
} from './types';
import { extractValidationErrors, VALIDATORS } from './validators';

export const RegistrationForm: React.FC<RegistrationFormProps> = ({
  onSuccess = redirectAfterLogin,
  ...props
}) => {
  const dispatch = useAppDispatch()
  // const ssoFeatureFlag = useFeatureFlag('enterprise_sso_form');
  const isMounted = useMountedState();
  const [isDisabled, setDisabled] = useState(false);
  const [submitLocked, setSubmitLocked] = useState(true);
  const [recaptchaInstanceId, setRecaptchaInstanceId] = useState(uuid());
  const [
    serializedForm,
    setSerializedForm,
  ] = useState<SerializedValues | null>();
  const [recaptchaError, setRecaptchaError] = useState<string | undefined>();

  const captcha = useRef<RecaptchaWrapper | null>(null);

  const { emailValidation, passwordValidation } = useMemo(
    () => ({
      emailValidation: VALIDATORS.email(),
      passwordValidation: VALIDATORS.password(),
    }),
    []
  );

  // if (ssoFeatureFlag) {
  //   return <RegistrationSSOForm onSuccess={onSuccess} />;
  // }

  const submitForm = (recaptchaToken: string) => {
    setDisabled(true);
    // request
    //   .post(registerPath)
    //   .send({ ...serializedForm, 'g-recaptcha-response': recaptchaToken })
    //   .use(csrf)
    //   .accept('json')
    //   .then(({ body }) => {
    //     // pushDataLayerEvent('user_sign_up');
    //     setDisabled(false);
    //     onSuccess(body);
    //   })
    //   .catch((err) => {
    //     setRecaptchaError(
    //       extractValidationErrors(err.response)?.['g-recaptcha-response']
    //     );
    //     setDisabled(false);
    //     resetRecaptcha();
    //   });
  };

  const resetRecaptcha = () => {
    // Reset recaptcha by re-rendering the component
    // using `recaptchaInstanceId` as a key
    // This is more predictable than calling captcha.reset()
    setDisabled(false);
    setRecaptchaInstanceId(uuid());
  };

  const onSubmitInitialize = (values: SubmitValues) => {
    // trackUserClick({ target: 'create_account' });

    setDisabled(true);
    const serializedValues = serializeRegistrationValues(values);
    setSerializedForm(serializedValues);

    if (captcha.current === null) {
      // logger.error({ message: 'captcha on checkoutpage has a null ref' });
      return;
    }
    captcha.current.execute();
    // validate the full form to see if we can submit
    setDisabled(false);
  };

  /**
   * @description
   * This unlocks submit a half second after the first input to prevent against bots - subsequent calls have no effect
   * */
  const onUpdate = () => {
    delay(() => isMounted() && submitLocked && setSubmitLocked(false), 500);
  };

  const { currentPage, locationType, redirectUrl } = props;
  const signingUpFromExercise = locationType === 'location/EXERCISE';
  // const safeRedirect = redirectUrl && safeRedirectUrl(redirectUrl);

  return (
    <div className={styles.registrationContainer}>
      <GridForm
        className={styles.loginForm}
        fields={[
          {
            name: UserSubmitKey.EMAIL,
            label: 'Email',
            type: 'email',
            defaultValue: '',
            size: 12,
            onUpdate,
            validation: emailValidation,
          },
          {
            name: UserSubmitKey.PASSWORD,
            label: 'Password',
            type: 'password',
            tooltip: {
              children: <Markdown text={passwordTips} />,
              id: 'password-tooltip',
              alignment: 'bottom-left',
              className: styles.tooltipContainer,
            },
            size: 12,
            onUpdate,
            defaultValue: '',
            validation: passwordValidation,
          },
          {
            name: 'referring_page',
            type: 'hidden',
            defaultValue: currentPage,
          },
          {
            name: 'redirect',
            type: 'hidden',
            defaultValue: {},
          },
          {
            name: 'authenticity_token',
            type: 'hidden',
            defaultValue: '',
          },
          {
            name: UserSubmitKey.CODING_REMINDERS_COMMS_TIMEZONE,
            type: 'hidden',
            defaultValue: Intl.DateTimeFormat().resolvedOptions().timeZone,
          },
          {
            name: 'signing_up_from_exercise',
            type: 'hidden',
            defaultValue: `${signingUpFromExercise}`,
          },
          {
            name: 'sweet_container',
            label: 'If you are a human, do not fill in this field.',
            type: 'sweet-container',
          },
        ]}
        submit={{
          contents: 'Sign Up',
          position: 'stretch',
          theme: 'brand-red',
          size: 12,
          disabled: isDisabled || submitLocked,
        }}
        validation="all"
        onSubmit={onSubmitInitialize}
      />
      <Box pt={16}>
        {recaptchaError && (
          <div className={styles.recaptchaError} >
            {recaptchaError}
          </div>
        )}
        {/* <RecaptchaWrapper
          key={recaptchaInstanceId}
          publicKey={''}
          size="invisible"
          ref={captcha}
          onChange={submitForm}
          onExpired={resetRecaptcha}
        /> */}
      </Box>
      <Text className={styles.textDisplay} >
        <p className={styles.textCenter}>
          Already have an account?{" "}
          <a className={styles.textLink} onClick={() => dispatch(showModal('login')) }>
            Sign in
          </a>
          .
        </p>
      </Text>

    </div>
  );
};
