import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/dist/query/react";
import { RootState } from "store";
import cookie from "react-cookies";

export interface CourseResponse {
  title: string;
  id: number;
  category: string;
  description: string;
}

export interface CourseRequest {
  title: string;
  category: string;
  description: string;
  cover_image: string;
  level: string;
  tags: string[];
  summary: string;
}

export interface Section {
  title: string;
  description: string;
}

export interface Lecture {
  title: string;
  video_url: string;
}

export interface CategoryResponse {
  title: string;
  id: number;
}

export const coursesApi = createApi({
  reducerPath: "courseApi",
  baseQuery: fetchBaseQuery({
    baseUrl: "/",
    prepareHeaders: (headers, { getState }) => {
      const csrfToken = cookie.load("csrftoken");

      if (csrfToken) {
        headers.set("X-CSRFToken", csrfToken);
      }
      const token =
        (getState() as RootState).auth.token || localStorage.getItem("token");
      if (token) {
        headers.set("Authorization", `Token ${token}`);
      }

      headers.set("Content-Type", "application/json");

      return headers;
    },
  }),
  tagTypes: [],
  endpoints: (build) => ({
    fetchCourse: build.query<CourseResponse, number>({
      query: (id) => ({
        url: `/courses/${id}`,
        method: "GET",
        responseHandler: (response) => response.json(),
      }),
    }),
    fetchCategories: build.query<CategoryResponse, void>({
      query: () => ({
        url: "/api/categories",
        method: "GET",
        responseHandler: (response) => response.json(),
      }),
    }),

    fetchCourse: build.query<CategoryResponse, void>({
      query: () => ({
        url: "/api/categories",
        method: "GET",
        responseHandler: (response) => response.json(),
      }),
    }),
    createCourse: build.mutation<CourseResponse, Partial<CourseRequest>>({
      query: (body) => ({
        url: "/courses/create/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),
    createSection: build.mutation<Section, Partial<Section>>({
      query: (body) => ({
        url: "/courses/section/create",
        body,
        method: "POST",
        responseHandler: (response) => response.json(),
      }),
    }),

    createLecture: build.mutation<Lecture, Partial<Lecture>>({
      query: (body) => ({
        url: "/courses/lecture/create",
        body,
        method: "POST",
        responseHandler: (response) => response.json(),
      }),
    }),
  }),
});

export const {
  useCreateCourseMutation,
  useCreateLectureMutation,
  useCreateSectionMutation,
  useFetchCourseQuery,
  useFetchCategoriesQuery,
} = coursesApi;
