import { useState } from "react";
import { Goal } from "./Goal";
import { Requirements } from "./Requirements";
export const Target = () => {
  const [goals, setGoals] = useState([{ id: "answerList--0", value: "" }]);
  const [requirements, setRequirements] = useState([
    { id: "answerList--0", value: "" },
  ]);
  const removeGoal = (goal) => {
    const updatedGoals = goals.filter((item) => item.id != goal.id);
    setGoals(updatedGoals);
  };

  const removeReq = (req) => {
    const updatedReqs = requirements.filter((item) => item.id != req.id);
    setRequirements(updatedReqs);
  };
  console.log('Reqs: ')
  return (
    <div>
      <div className="sub-header--wrapper--3Vunm">
        <div className="sub-header--main-content--22it3">
          <h2
            data-purpose="page-title"
            className="font-heading-serif-xl sub-header--title--2VD8q"
          >
            Target your students
          </h2>
        </div>
      </div>
      <div className="main-content--wrap_component--2TEkz">
        {" "}
        <div className="form_container">
          <div className="goals-form--subtitle--3R5d-">
            The descriptions you write here will help students decide if your
            course is the one for them.
          </div>
          <form className>
            <div>
              <div className="form-group">
                <label htmlFor="learn-goal" className="control-label">
                  What will students learn in your course?
                </label>
                <div>
                  <div>
                    {goals.map((goal) => (
                      <Goal
                        goal={goal}
                        handleRemove={removeGoal}
                        handleChange={(obj) => {
                          const newGoals = goals.map((item) => {
                            if (item.id == obj.id) {
                              item.value = obj.value;
                            }
                            return item;
                          });
                          setGoals(newGoals);
                        }}
                      />
                    ))}
                  </div>
                </div>
              </div>
              <button
                onClick={() => {
                  const answer = {
                    id: `answerList--${goals.length}`,
                    value: "",
                  };
                  const newGoals = [...goals, answer];
                  const emptyValue = goals.filter((item) => item.value == "");
                  if (emptyValue.length) {
                    return;
                  } else {
                    setGoals(newGoals);
                  }
                }}
                type="button"
                className="goals-form--button--2_zb1 btn btn-tertiary"
              >
                <span className="mr5 cfi cfi-add" /> Add an answer
              </button>
            </div>
            <div>
              <div className="form-group">
                <label htmlFor="requirements" className="control-label">
                  Are there any course requirements or prerequisites?
                </label>
                <div>
                  <div>
                    {requirements.map((req) => (
                      <Requirements
                        req={req}
                        handleRemove={removeReq}
                        handleChange={(obj) => {
                          const newReqs = requirements.map((item) => {
                            if (item.id == obj.id) {
                              item.value = obj.value;
                            }
                            return item;
                          });
                          setRequirements(newReqs);
                        }}
                      />
                    ))}
                  </div>
                </div>
              </div>
              <button
                onClick={() => {
                  const answer = {
                    id: `answerList--${requirements.length}`,
                    value: "",
                  };
                  const newReqs = [...requirements, answer];
                  const emptyValue = requirements.filter((item) => item.value == "");
                  if (emptyValue.length) {
                    return;
                  } else {
                    setRequirements(newReqs);
                  }
                }}
                type="button"
                className="goals-form--button--2_zb1 btn btn-tertiary"
              >
                <span className="mr5 cfi cfi-add" /> Add an answer
              </button>
            </div>
            <div>
              <div className="form-group">
                <label htmlFor="target-student" className="control-label">
                  Who are your target students?
                </label>
                <div>
                  <div>
                    <div className="goals-form--sortable-answer--PLpb9 goals-form--hide-buttons--PHgbN form-group">
                      <span className="input-group">
                        <input
                          placeholder="Example: Beginner Python developers curious about data science"
                          data-purpose="target-student-input-answerList--22"
                          id="answerList--22"
                          className="form-control"
                        />
                        <span className="input-group-btn">
                          <button
                            id="answerList--22"
                            type="button"
                            className="btn btn-default"
                          >
                            <span
                              id="answerList--22"
                              className="cfi cfi-trash-o"
                            />
                          </button>
                          <button
                            type="button"
                            className="js-drag-handle goals-form--sortable-answer__handle--2dOgs btn btn-default"
                          >
                            <span className="cfi cfi-bars" />
                          </button>
                        </span>
                      </span>
                    </div>
                  </div>
                </div>
              </div>
              <button
                data-purpose="add-target-student"
                type="button"
                className="goals-form--button--2_zb1 btn btn-tertiary"
              >
                <span className="mr5 cfi cfi-add" /> Add an answer
              </button>
            </div>
          </form>
        </div>{" "}
      </div>
    </div>
  );
};

export default Target;
