import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import { head } from "lodash";
import cookie from "react-cookies";
import { RootState } from "store";
import internal from "stream";

export interface LoginRequest {
  email: string;
  password: string;
  csrfmiddlewaretoken:string;
}

export interface Signup {
  email: string;
  username: string;
  password1: string;
  password2: string;
  csrfmiddlewaretoken:string;
}

export interface Token {
  key: string;
}

export interface User {
  pk:number;
  email: string;
  username:string;
  first_name:string;
  last_name:string;

}

export interface UserResponse {
  user: User,
  token: string
}

export interface Logout {

  csrfmiddlewaretoken:string;
}

export interface LogoutResponse {
  detail: string;
}

export const authApi = createApi({
  baseQuery: fetchBaseQuery({
    baseUrl: "/",
    prepareHeaders: (headers, { getState }) => {
      const csrfToken = cookie.load("csrftoken");


      if (csrfToken) {
        headers.set("X-CSRFToken", csrfToken);

      }
      const token = (getState() as RootState).auth.token || localStorage.getItem('token');
      if(token) {
        headers.set("Authorization", `Token ${token}`)
      }

      headers.set("Content-Type", "application/json");

      return headers;
    },
  }),
  tagTypes: ["LoginRequest", "Signup", "Logout", "User", "UserResponse"],
  endpoints: (build) => ({
    login: build.mutation<UserResponse, Partial<LoginRequest>>({
      query: (body) => ({
        url: "auth/login/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),
    signup: build.mutation<Signup, Partial<Signup>>({
      query: (body) => ({
        url: "rest-auth/registration/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),
    logout: build.mutation<LogoutResponse, Partial<Logout>>({
      query: (body) => ({
        url: "rest-auth/logout/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),
    getCurrentUser: build.query<User, void>({
      query: () => ({
        url: "rest-auth/user/",
        method: "GET",

        responseHandler: (response) => response.json(),
      }),
    }),
  }),
});

export const { getCurrentUser } = authApi.endpoints;

export const { useLoginMutation, useSignupMutation, useLogoutMutation, useGetCurrentUserQuery,  } = authApi;
