import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { v1 as uuid } from "uuid";
import { StringChain, stubFalse, without } from "lodash";
import { AppThunk, RootState } from "store";
import { clamp } from "portal/scenes/CoursePlayer/utilities/clamp";
import {
  InteractionModes,
  Defaults,
} from "portal/scenes/CoursePlayer/constants";

const validModes = Object.values(InteractionModes);

function includes(modes, mode) {
  return !!~modes.indexOf(mode);
}
export interface PlayerState {
  playing: boolean;
  playbackSpeed: number;
  activeMenu: string;
  volumeSliderActive: boolean;
  volume: number;
  muted: boolean;
  previousVolume: number;
  interactionMode: string;
  time: number;
  duration: number;
  playheadStart: number;
  overlay: Overlay;
  videoPlayer: HTMLVideoElement;
  seekEvent: Event,
  seekingEvent: Event
}

export interface Overlay {
  icon: string;
  key: number;
  fade: boolean;
}

export const initialState: PlayerState = {
  playing: false,
  playbackSpeed: 1.0,
  activeMenu: null,
  volumeSliderActive: false,
  volume: 0.5,
  muted: false,
  previousVolume: null,
  interactionMode: InteractionModes.MOUSE,
  time: 0,
  duration: 0,
  playheadStart: 0,
  overlay: null,
  videoPlayer: null,
};
export const playerSlice = createSlice({
  name: "player",
  initialState,
  reducers: {
    play: (state, action: PayloadAction<void>) => {
      state.playing = true;
    },

    pause: (state, action: PayloadAction<void>) => {
      state.playing = false;
    },
    setPlaybackSpeed: (state, action: PayloadAction<number>) => {
      state.playbackSpeed = action.payload;
    },

    setActiveMenu: (state, action: PayloadAction<string>) => {
      state.activeMenu = action.payload;
    },
    setVolumeSliderActive: (state, action: PayloadAction<boolean>) => {
      state.volumeSliderActive = action.payload;
    },
    setVolume: (state, action: PayloadAction<number>) => {
      const clamped = clamp(action.payload, 0, 1);
      const muted = clamped === 0;
      state.volume = clamped;
      state.muted = muted;
    },
    setPreviousVolume: (state, action: PayloadAction<number>) => {
      const clamped = clamp(action.payload, 0, 1);
      const muted = clamped === 0;
      state.volume = clamped;
      state.muted = muted;
    },
    setInteractionMode: (state, action: PayloadAction<string>) => {
      if (!includes(validModes, action.payload)) {
        throw new Error(
          `${action.payload} not in valid list: ${validModes.join()}`
        );
      }
      state.interactionMode = action.payload;
    },

    setDuration: (state, action: PayloadAction<number>) => {
      state.duration = action.payload;
    },
    setTime: (state, action: PayloadAction<number>) => {
      state.time = action.payload;
    },

    ready: (state, action: PayloadAction<number>) => {
      state.playheadStart = state.time;
      state.duration = action.payload;
    },
    registerPlayer: (state, action: PayloadAction<HTMLVideoElement>) => {
      const seekedEvent = new Event("ps-seeked");
      const seekingEvent = new Event("ps-seeking");

      return { seekedEvent, seekingEvent, player };
    },
    fastForward: (state, action: PayloadAction<number>) => {
      const { time, duration } = state;

      const forwardTime = time + 10;

      const newTime = forwardTime > duration ? duration : forwardTime;
      state.playheadStart = newTime;
      state.time = newTime;
      state.overlay = {
        icon: "FWD",
        key: Math.random(),
        fade: true,
      };
    },

    fastRewind: (state, action: PayloadAction<number>) => {
      const { time, duration } = state;

      const startTime = 0;
      const rewindTime = time - 10;

      const newTime = rewindTime < startTime ? startTime : rewindTime;
      state.playheadStart = newTime;
      state.time = newTime;
      state.overlay = {
        icon: "BACK",
        key: Math.random(),
        fade: true,
      };
    },

    toggleMute: (state, action: PayloadAction<void>) => {
      if (state.muted) {
        setVolume(state.previousVolume || Defaults.volume);
      }
      state.muted = true;
      state.previousVolume = state.volume;
      state.volume = 0;
    },
  },
});

export const selectPlayer = (state: RootState) => state.player;

export const {
  play,
  pause,
  setPlaybackSpeed,
  setActiveMenu,
  setVolumeSliderActive,
  setVolume,
  setPreviousVolume,
  toggleMute,
  setDuration,
  setTime,
  ready,
  fastForward,
  fastRewind,
} = playerSlice.actions;

export default playerSlice.reducer;
