import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/dist/query/react";
import algoliasearch from 'algoliasearch/lite';


import { RootState } from "store";
import cookie from "react-cookies";


const searchClient = algoliasearch(
  process.env.REACT_APP_ALGOLIA_APP_ID,
  process.env.REACT_APP_ALGOLIA_SEARCH_KEY
);

export interface CourseResponse {
  title: string;
  id: number;
  category: string;
  description: string;
}

export interface Media {
  course: number;
  name: string;
  filestype: string;
  key: string;



}

export interface SearchResponse {
  title:string;
  tags: string[];
}

export interface CourseRequest {
  title: string;
  category: string;
  description: string;
  cover_image: string;
  level: string;
  tags: string[];
  summary: string;
}

export interface Section {
  title: string;
  description: string;
}

export interface Options {
  name: string;
  display: string
}

export interface Lecture {
  title: string;
  video_url: string;
}

export interface CategoryResponse {
  title: string;
  id: number;
  children: CategoryResponse[]
}

export const autoSearch = (query: string) => {
  searchClient.initIndex('tags_index').search(query)
  .then(({ hits}) => console.log("Hits: ", hits))
  .catch(err => console.log('Error: ', err))
}


export const coursesApi = createApi({
  reducerPath: "courseApi",
  baseQuery: fetchBaseQuery({
    baseUrl: "/",
    prepareHeaders: (headers, { getState }) => {
      const csrfToken = cookie.load("csrftoken");

      if (csrfToken) {
        headers.set("X-CSRFToken", csrfToken);
      }
      const token =
        (getState() as RootState).auth.token || localStorage.getItem("token");
      if (token) {
        headers.set("Authorization", `Token ${token}`);
      }

      headers.set("Content-Type", "application/json");

      return headers;
    },
  }),
  tagTypes: [],
  endpoints: (build) => ({
    fetchCourse: build.query<CourseResponse, number>({
      query: (id) => ({
        url: `/courses/${id}`,
        method: "GET",
        responseHandler: (response) => response.json(),
      }),
    }),
    fetchCategories: build.query<CategoryResponse[], void>({
      query: () => ({
        url: "/api/categories",
        method: "GET",
        responseHandler: (response) => response.json(),
      }),
    }),

    fetchCourseLevel: build.query<Options[], void>({
      query: () => ({
        url: "/courses/level",
        method: "GET",
        responseHandler: (response) => response.json(),
      }),
    }),

    searchTags: build.query<SearchResponse[], string>({
      query: (q) => ({
        url: `/courses/search?q=${q}`,
        method: "GET",
        responseHandler: (response) => response.json(),
      }),
    }),
    createCourse: build.mutation<CourseResponse, Partial<CourseRequest>>({
      query: (body) => ({
        url: "/courses/create/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),

    uploadLectureVideo: build.mutation<CourseResponse, Partial<CourseRequest>>({
      query: (body) => ({
        url: "/courses/create/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),
    createSection: build.mutation<Section, Partial<Section>>({
      query: (body) => ({
        url: "/courses/section/create",
        body,
        method: "POST",
        responseHandler: (response) => response.json(),
      }),
    }),

    createLecture: build.mutation<Lecture, Partial<Lecture>>({
      query: (body) => ({
        url: "/courses/lecture/create",
        body,
        method: "POST",
        responseHandler: (response) => response.json(),
      }),
    }),
  }),
});

export const {
  useCreateCourseMutation,
  useCreateLectureMutation,
  useCreateSectionMutation,
  useFetchCourseQuery,
  useFetchCategoriesQuery,
  useFetchCourseLevelQuery,
  useSearchTagsQuery
} = coursesApi;
