        <label htmlFor="description" className="control-label">
          Lecture Description
        </label>
        <RTEditor editorValue={""} handleChange={() => console.log()}/>
      </div>
      <div className="text-right">
        <button type="button" className="mr5 btn btn-sm btn-default">
          Cancel
        </button>
        <button type="submit" className="btn btn-sm btn-secondary">
          Save
        </button>
      </div>
    </form>
  );
};
