import { useEffect, useState } from "react";
import { useAlert } from "react-alert";


import { Included } from "./Includeded";
import { CreateIncluded } from "./CreateIncluded";
import { Syllabus } from "./Syllabus";
import { SyllabusCreate } from "./SyllabusCreate";
import { CreateOutcome } from "./CreateOutcome";

import { Outcome } from "./Outcome";

import { selectSave } from "state/course/courseSplice";

import {
  selectLocationPayload,
  selectLocationType,
} from "state/location/selectors";
import { useAppSelector } from "store/hooks";

import {
  useCreateIncludedMutation,
  useCreateLearningOutcomeMutation,
  useCreateSyllabusMutation,
  useEditSyllabusMutation,
  useEditIncludedMutation,
  useDeleteOutcomeMutation,
  useEditLearningOutcomeMutation,
  useFetchIncludedQuery,
  useDeleteSyllabusMutation,
  useDeleteIncludedMutation,
  useFetchSyllabusQuery,
  useFetchOutcomesQuery,
  TitleDescription,
} from "services/projects";

import {
  selectOutcomes,
  selectIncluded,
  selectSyllabuses,
} from "state/project/projectSplice";

export const Target = () => {
  const locationPath = useAppSelector(selectLocationType);
  const selectedSave = useAppSelector(selectSave);
  const locationPayload = useAppSelector(selectLocationPayload);

  const selectedOutcomes = useAppSelector(selectOutcomes);
  const selectedIncluded = useAppSelector(selectIncluded);
  const selectedSyllabuses = useAppSelector(selectSyllabuses);
  const [createSyllabus] = useCreateSyllabusMutation();
  const [createLearningOutcome] = useCreateLearningOutcomeMutation();
  const [createIncluded] = useCreateIncludedMutation();
  const [editSyllabus] = useEditSyllabusMutation();
  const [editLearningOutcome] = useEditLearningOutcomeMutation();
  const [editIncluded] = useEditIncludedMutation();
  const [deleteSyllabus] = useDeleteSyllabusMutation();
  const [deleteOutcome] = useDeleteOutcomeMutation();
  const [deleteIncluded] = useDeleteIncludedMutation();

  const { data: syllabusQuery } = useFetchSyllabusQuery(locationPayload.id);
  const { data: learningQuery } = useFetchOutcomesQuery(locationPayload.id);
  const { data: IncludedQuery } = useFetchIncludedQuery(locationPayload.id);

  const [syllabusUpdate, setSyllabusUpdate] = useState(selectedSyllabuses);
  const [learningUpdate, setLearningUpdate] = useState(selectedOutcomes);
  const [includeUpdate, setIncludeUpdate] = useState(selectedIncluded);

  const alert = useAlert();

  const [syllabuses, setSyllabuses] = useState<TitleDescription[]>([
    { id: -1, title: "", description: "", project: locationPayload.id },
  ]);
  const [outcomes, setOutcomes] = useState<TitleDescription[]>([
    { id: -1, title: "", description: '', project: locationPayload.id },
  ]);

  const [included, setIncluded] = useState<TitleDescription[]>([{ id: -1, title: '', description: '', project: locationPayload.id }]);

  const removeSyllabus = (syllabus) => {
    deleteSyllabus(syllabus.id);
  };

  const removeOutcome = (req) => {
    deleteOutcome(req.id);
  };

  const removeIncluded = (req) => {
    deleteIncluded(req.id);
  };

  useEffect(() => {
    setLearningUpdate(selectedOutcomes);
  }, [selectedOutcomes]);

  useEffect(() => {
    setIncludeUpdate(selectedIncluded);
  }, [selectedIncluded]);

  useEffect(() => {
    setSyllabusUpdate(selectedSyllabuses);
  }, [selectedSyllabuses]);

  useEffect(() => {
    if(selectedSave.submit) {

      const syllabusResults = syllabuses;
      syllabusResults &&
        syllabusResults.forEach((syllabus) => {
          if (syllabus.title) {
            createSyllabus({
              title: syllabus.title,
              description: syllabus.description,
              project: locationPayload.id,
            }).then(() => {
              const results = syllabusResults.filter(
                (item) => item.id != syllabus.id
              );
              setSyllabuses(results);
              alert.show("Your data has been saved successfully");
            });
          }
        });

      const learningOutcomes = outcomes;
      learningOutcomes &&
        learningOutcomes.forEach((exp) => {
          if (exp.title) {
            createLearningOutcome({
              title: exp.title,
              project: locationPayload.id,
            }).then(() => {
              const results = learningOutcomes.filter(
                (item) => item.id != exp.id
              );
              setOutcomes(results);
              alert.show("Your data has been saved successfully");
            });
          }
        });

      const incResults = included;
      incResults &&
        incResults.forEach((req) => {
          if (req.title) {
            createIncluded({
              title: req.title,
              project: locationPayload.id,
            }).then(() => {
              const results = incResults.filter((item) => item.id != req.id);
              setIncluded(results);
              alert.show("Your data has been saved successfully");
            });
          }
        });
      syllabusUpdate &&
        syllabusUpdate.forEach((req) => {
          if (req.title) {
            console.log("Req: ", req);
            editSyllabus({
              ...req,
              project: locationPayload.id,
            }).then(() => {
              alert.show("Your data has been saved successfully");
            });
          }
        });

      learningUpdate &&
        learningUpdate.forEach((req, index) => {
          if (req.title) {
            editLearningOutcome({
              ...req,
              project: locationPayload.id,
            }).then(() => {
              alert.show("Your data has been saved successfully");
            });
          }
        });

      includeUpdate &&
        includeUpdate.forEach((item) => {
          if (item.title) {
            editIncluded({
              ...item,
              project: locationPayload.id,
            }).then(() => {
              alert.show("Your data has been saved successfully");
            });
          }
        });

    }

  }, [selectedSave]);
  return (
    <div>
      <div className="sub-header--wrapper--3Vunm">
        <div className="sub-header--main-content--22it3">
          <h2
            data-purpose="page-title"
            className="font-heading-serif-xl sub-header--title--2VD8q"
          >
            Target your students
          </h2>
        </div>
      </div>
      <div className="main-content--wrap_component--2TEkz">

        <div className="form_container">
          <div className="goals-form--subtitle--3R5d-">
            The descriptions you write here will help students decide if your
            project is the one for them.
          </div>
          <form>
            <div>
            <li className="js-curriculum-item-draggable curriculum-list--curriculum-list__section--2SsIO curriculum-list--curriculum-list__section--inline--2E-oq">
        <div className="curriculum-list--item-wrap--1GkZz">
          <div className="curriculum-list--add-item-section-wrapper--bXWiB">
            <div
              className={classNames("curriculum-list--wrapper-section--3v7bx", {
                "curriculum-list--open--VjfTb": section == "newSection01",
              })}
            >
              <button
                id={"newSection01"}
                aria-label="New curriculum item"
                onClick={(event) => {
                  const target = event.target as Element
                  setSection(target.id);
                }}
                type="button"
                className={classNames(
                  "curriculum-list--add-item-section--3JmsU add-item--add-item-toggler--1k-rh add-item--section--1_IVB btn btn-xs btn-link",
                  { "add-item--open--2FcI6": section == "newSection01" }
                )}
              >
                <span
                  className={classNames(
                    "add-item--icon--2FsmW cfi-small cfi cfi-close",
                    {
                      "add-item--open--2FcI6": section == "newSection01",
                      "add-item--rotate--WJ08z": section != "newSection01",
                    }
                  )}
                />
              </button>
            </div>
            {section == "newSection01" && (
              <SectionCreate
                index={index}
                position="above"
                addSection={addSection}
                className={"curriculum-list--over-section--1DyMX"}
                toggle={() => setSection("")}
              />
            )}
          </div>
          <div data-purpose="section-editor">
            {sectionEdit && (
              <SectionEdit
                index={index}
                editSection={editSection}
                section={sectionResult}
                toggle={() => setSectionEdit(!sectionEdit)}
              />
            )}
            <div className="fx-lt item-icon-button-trigger js-curriculum-item-handle item-bar--item-bar--1qWKU item-bar--item-bar--section--3aQ_4">
              <div className="fx">
                <div className="fx-lt fxwrap">
                  <div className="item-bar--item-bar__left--1nfEv">
                    <div className="fx-lc item-bar--item-bar__title--3ww0E">
                      <span className="item-bar--item-bar__status--MZ70P">
                        <span data-purpose="empty-status-icon">&nbsp;</span>
                        {`Section ${index + 1}:`}
                      </span>
                      <span className="hidden-xxs ellipsis pl10">
                        <span className="pr5 cfi cfi-file-text-o" />
                        <span>{sectionResult.title}</span>
                      </span>
                      <button
                        onClick={() => {
                          setSectionEdit(!sectionEdit);
                        }}
                        data-purpose="section-edit-btn"
                        type="button"
                        className="item-icon-button--icon-button--1IKG6 btn btn-xs btn-quintinary"
                      >
                        <span className="cfi cfi-pencil" />
                        <span className="sr-only">Edit</span>
                      </button>
                      <button
                        onClick={() => setToggleDelete(!toggleDelete)}
                        type="button"
                        className="item-icon-button--icon-button--1IKG6 btn btn-xs btn-quintinary"
                      >
                        <span className="cfi cfi-trash-o" />
                        <span className="sr-only">Delete</span>
                      </button>
                    </div>
                    <div className="visible-xxs ellipsis pb5">
                      <span
                        data-purpose="section-icon"
                        className="pr5 cfi cfi-file-text-o"
                      />
                      <span>GraphQL Fundamentals</span>
                    </div>
                  </div>
                </div>
              </div>
              <div className="item-bar--item-bar__right--1gA3p">
                <div
                  data-type="button"
                  className="item-bar--item-bar__handle--1DqDp item-icon-button--icon-button--1IKG6 btn btn-xs btn-quintinary"
                >
                  <span className="cfi cfi-bars" />
                  <span className="sr-only">Move</span>
                </div>
              </div>
            </div>
          </div>
          <div className="curriculum-list--add-item-wrapper--2CGL-">
            <div
              className={classNames("curriculum-list--wrapper--2Jw7f", {
                "curriculum-list--open--VjfTb": lecture == "newLecture01",
              })}
            >
              <button
                id={"newLecture01"}
                onClick={(event) => {
                  const target = event.target as Element
                  setLecture(target.id);
                }}
                aria-label="New curriculum item"
                type="button"
                className={classNames(
                  "curriculum-list--add-item--1DK2G add-item--add-item-toggler--1k-rh add-item--lecture--2ZWW9 btn btn-xs btn-link",
                  {
                    "add-item--open--2FcI6": lecture == "newLecture1",
                  }
                )}
              >
                <span
                  className={classNames(
                    "add-item--icon--2FsmW  cfi-small cfi cfi-close",
                    {
                      "add-item--rotate--WJ08z": lecture != "newLecture01",
                      "add-item--open--2FcI6": lecture == "newLecture01",
                    }
                  )}
                />
              </button>
            </div>
            <div>
              {lecture == "newLecture01" && (
                <LectureCreate
                  position='above'
                  handleChange={(value) => {
                    var id = null
                    if(lectures && lectures.length) {
                      id = lectures[0].id
                    }

                    createLecture({
                      title: value.title,
                      position: value.position,
                      neighbor: id,
                      section: sectionResult.id
                    })
                  }}
                  toggle={() => setLecture("")}
                />
              )}
            </div>
          </div>
        </div>
      </li>
      {lectures &&
        lectures.map((lec, index) => (
          <li
            id={`${index}`}
            key={index}
            className="js-curriculum-item-draggable curriculum-list--curriculum-list__section-item--1hfk_ curriculum-list--curriculum-list__section-item--inline--aFpJo"
          >
            <div className="curriculum-list--item-wrap--1GkZz">
              <Lecture lecture={lec} index={index} editMedia={result => {
                editLecture({
                  id: result.id,
                  title: lec.title,
                  video: result.video,
                  video_url: result.video_url
                })
              }} editLecture={value => editLecture({
                title: value.title,
                id: value.id,
                resources: value.resources,
                section: sectionResult.id
              })}/>
              <div className="curriculum-list--add-item-wrapper--2CGL-">
                <div
                  className={classNames("curriculum-list--wrapper--2Jw7f", {
                    "curriculum-list--open--VjfTb":
                      lecture == `newLecture${index}`,
                  })}
                >
                  <button
                    id={`newLecture${index}`}
                    onClick={(event) => {
                      const target = event.target as Element
                      setLecture(target.id);
                    }}
                    aria-label="New curriculum item"
                    data-purpose="add-item-inline"
                    type="button"
                    className={classNames(
                      "curriculum-list--add-item--1DK2G add-item--add-item-toggler--1k-rh add-item--lecture--2ZWW9 btn btn-xs btn-link",
                      {
                        "add-item--open--2FcI6":
                          lecture == `newLecture${index}`,
                      }
                    )}
                  >
                    <span
                      className={classNames(
                        "add-item--icon--2FsmW  cfi-small cfi cfi-close",
                        {
                          "add-item--rotate--WJ08z":
                            lecture != `newLecture${index}`,
                          "add-item--open--2FcI6":
                            lecture == `newLecture${index}`,
                        }
                      )}
                    />
                  </button>
                </div>
              </div>
              <div>

              </div>
              <div className="curriculum-list--add-item-section-wrapper--bXWiB">
                <div
                  className={classNames(
                    "curriculum-list--wrapper-section--3v7bx curriculum-list--is-last-item--1Diog",
                    {
                      "curriculum-list--open--VjfTb":
                        section == `newSection${index}`,
                    }
                  )}
                >
                  <button
                    id={`newSection${index}`}
                    onClick={(event) => {
                      const target = event.target as Element
                      index == lectures.length - 1
                        ? setSection(target.id)
                        : setLecture(target.id);
                    }}
                    aria-label="New curriculum item"
                    data-purpose="add-item-inline"
                    type="button"
                    className={classNames(
                      "curriculum-list--add-item-section--3JmsU add-item--add-item-toggler--1k-rh add-item--section--1_IVB btn btn-xs btn-link",
                      {
                        "add-item--open--2FcI6":
                          section == `newSection${index}`,
                      }
                    )}
                  >
                    <span
                      // className={classNames(
                      //   "add-item--icon--2FsmW cfi-small cfi cfi-close",
                      //   {
                      //     "add-item--open--2FcI6":
                      //       section == `newSection${index}`,
                      //     "add-item--rotate--WJ08z":
                      //       section != `newSection${index}`,
                      //   }
                      // )}
                    />
                  </button>
                </div>
                {/* {section == `newSection${index}` && (
                  <SectionCreate
                    index={index}
                    position="below"
                    addSection={addSection}
                    className={"curriculum-list--under-section--zkvIv"}
                    toggle={() => setSection("")}
                  />
                )} */}
              </div>
            </div>
          </li>
        ))}

        />
              <div className="form-group">
                <label htmlFor="learn-goal" className="control-label">
                  What will students learn in your project?
                </label>
                <div>
                  <div>
                    {syllabusUpdate &&
                      syllabusUpdate.map((syllabus) => (
                        <Syllabus
                          key={syllabus.id}
                          syllabus={syllabus}
                          handleRemove={removeSyllabus}
                          handleChange={(obj) => {
                            let gUpdate = [...syllabusUpdate];
                            let index = gUpdate.findIndex((item) => {
                              return item.id == obj.id;
                            });
                            gUpdate[index] = obj;
                            setSyllabusUpdate(gUpdate);
                          }}
                        />
                      ))}
                    {syllabuses.map((syllabus) => (
                      <SyllabusCreate
                        key={syllabus.id}
                        syllabus={syllabus}
                        handleRemove={(value) => {
                          const updatedGoals = syllabuses.filter(
                            (result) => result.id != value.id
                          );
                          setSyllabuses(updatedGoals);
                        }}
                        handleChange={(obj) => {
                          let newSyllabus = [...syllabuses];
                          let index = newSyllabus.findIndex((item) => {
                            return item.id == obj.id;
                          });
                          newSyllabus[index] = obj;
                          setSyllabuses(newSyllabus);
                        }}
                      />
                    ))}
                  </div>
                </div>
              </div>
              <button
                onClick={() => {
                  const syllabusItem = {
                    id: + syllabuses.length,
                    title: "",
                    description: "",
                    project: locationPayload.id

                  };
                  const newSyllabus = [...syllabuses, syllabusItem];
                  const emptyValue = syllabuses.filter(
                    (item) => item.title == ""
                  );
                  if (emptyValue.length) {
                    return;
                  } else {
                    setSyllabuses(newSyllabus);
                  }
                }}
                type="button"
                className="goals-form--button--2_zb1 btn btn-tertiary"
              >
                <span className="mr5 cfi cfi-add" /> Add an answer
              </button>
            </div>
            <div>
              <div className="form-group">
                <label htmlFor="requirements" className="control-label">
                  What will be the important takeaways?
                </label>
                <div>
                  <div>
                    {learningUpdate &&
                      learningUpdate.map((req) => (
                        <Outcome
                          outcome={req}
                          key={req.id}
                          handleRemove={removeOutcome}
                          handleChange={(obj) => {
                            let reqUpdates = [...learningUpdate];
                            let index = reqUpdates.findIndex((item) => {
                              return item.id == obj.id;
                            });
                            reqUpdates[index] = obj;
                            setLearningUpdate(reqUpdates);
                          }}
                        />
                      ))}

                    {outcomes.map((req) => (
                      <CreateOutcome
                        outcome={req}
                        key={req.id}
                        handleRemove={(value) => {
                          const updatedReqs = outcomes.filter(
                            (result) => result.id != value.id
                          );
                          setOutcomes(updatedReqs);
                        }}
                        handleChange={(obj) => {
                          let reqs = [...outcomes];
                          let index = reqs.findIndex((item) => {
                            return item.id == obj.id;
                          });
                          reqs[index] = obj;
                          setOutcomes(reqs);
                        }}
                      />
                    ))}
                  </div>
                </div>
              </div>
              <button
                onClick={() => {
                  const reqItem = {
                    id: -1 + outcomes.length,
                    title: "",
                    description: "",
                    project: locationPayload.id
                  };
                  const newReqs = [...outcomes, reqItem];
                  const emptyValue = outcomes.filter((item) => item.title == "");
                  if (emptyValue.length) {
                    return;
                  } else {
                    setOutcomes(newReqs);
                  }
                }}
                type="button"
                className="goals-form--button--2_zb1 btn btn-tertiary"
              >
                <span className="mr5 cfi cfi-add" /> Add an answer
              </button>
            </div>
            <div>
              <div className="form-group">
                <label htmlFor="target-student" className="control-label">
                  Let your students know what is included in this project?
                </label>
                <div>
                  <div>
                    {includeUpdate &&
                      includeUpdate.map((exp) => (
                        <Included
                          key={exp.id}
                          inc={exp}
                          handleRemove={removeIncluded}
                          handleChange={(obj) => {
                            let expUpdate = [...includeUpdate];

                            let index = expUpdate.findIndex((item) => {
                              return item.id == obj.id;
                            });
                            expUpdate[index] = obj;
                            setIncludeUpdate(expUpdate);
                          }}
                        />
                      ))}

                    {included.map((exp) => (
                      <CreateIncluded
                        key={exp.id}
                        inc={exp}
                        handleRemove={(value) => {
                          const updatedIncluded = included.filter(
                            (result) => result.id != value.id
                          );
                          setIncluded(updatedIncluded);
                        }}
                        handleChange={(obj) => {
                          let expdate = [...included];

                          let index = expdate.findIndex((item) => {
                            return item.id == obj.id;
                          });
                          expdate[index] = obj;
                          setIncluded(expdate);
                        }}
                      />
                    ))}
                  </div>
                </div>
              </div>
              <button
                onClick={() => {
                  const expItem = {
                    id: included.length,
                    title: "",
                    description: "",
                    project: locationPayload.id
                  };
                  const newExps = [...included, expItem];
                  const emptyValue = included.filter(
                    (item) => item.title == ""
                  );
                  if (emptyValue.length) {
                    return;
                  } else {
                    setIncluded(newExps);
                  }
                }}
                type="button"
                className="goals-form--button--2_zb1 btn btn-tertiary"
              >
                <span className="mr5 cfi cfi-add" /> Add an answer
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Target;
