import "@pluralsight/ps-design-system-normalize";

import React, { Fragment, useMemo } from "react";

import AppFrame from "@pluralsight/ps-design-system-appframe";
import * as core from "@pluralsight/ps-design-system-core";
import * as Layout from "@pluralsight/ps-design-system-layout";
import { AsideLayout } from "@pluralsight/ps-design-system-layout";
import * as Icon from "@pluralsight/ps-design-system-icon";
import NavBar from "@pluralsight/ps-design-system-navbar";
import NavBrand from "@pluralsight/ps-design-system-navbrand";
import NavItem from "@pluralsight/ps-design-system-navitem";
import NavUser from "@pluralsight/ps-design-system-navuser";
import * as Text from "@pluralsight/ps-design-system-text";
import Theme from "@pluralsight/ps-design-system-theme";
import VerticalTabs from "@pluralsight/ps-design-system-verticaltabs";
import Steps from "@pluralsight/ps-design-system-steps";
import styles from './styles.module.scss';
import useMatchMedia from "./use-match-media";
import { createUniversalPortal } from "@pluralsight/ps-design-system-util";
import NavCookieBanner from "@pluralsight/ps-design-system-navcookiebanner";
import CourseDescription from "./description";
const canUseDOM = () =>
  !!(
    typeof window !== "undefined" &&
    window.document &&
    window.document.createElement
  );
const root = canUseDOM() ? document.body : null;

export default function App() {
  const { steps, select } = useData();
  return (
    <Theme name={Theme.names.light}>
      <CourseFrame>
        <AsideLayout
          className={styles.layout}
          aside={
            <AsideLayout.Aside>
              <Steps orientation="vertical">
                {steps.map((step, index) => (
                  <Steps.Step
                    key={index}
                    description={step.status === "current" && step.description}
                    onClick={() => select(step)}
                    status={step.status}
                  >
                    {step.title}
                  </Steps.Step>
                ))}
              </Steps>
            </AsideLayout.Aside>
          }
          main={
            <AsideLayout.Main className={styles.sideLayout} >
              <div style={{ background: core.colorsPink[6] }}>
                <Layout.PageWidthLayout>
                  <Layout.PageHeadingLayout
                    heading={<h1 style>Course Description</h1>}
                  />
                </Layout.PageWidthLayout>
              </div>
              <Layout.PageWidthLayout>
                <CourseDescription />
              </Layout.PageWidthLayout>
            </AsideLayout.Main>
          }
        />
      </CourseFrame>
    </Theme>
  );
}

function CourseFrame(props) {
  const mobile = !useMatchMedia(`(min-width: ${core.breakpoints.xSmall})`);

  const sidenavSections = useMemo(
    () =>
      mobile
        ? [{ items: MAIN_NAV_ITEMS }, ...SIDE_NAV_SECTIONS]
        : SIDE_NAV_SECTIONS,
    [mobile]
  );

  const topnavItems = useMemo(() => (mobile ? [] : MAIN_NAV_ITEMS), [mobile]);

  return (
    <>
      <AppFrame
        topnav={({ openSidenav, closeSidenav, sidenavOpen }) => {
          const toggle = sidenavOpen ? closeSidenav : openSidenav;
          return (
            <SkillsTopNav items={topnavItems} onMobileMenuClick={toggle} />
          );
        }}

      >
        {props.children}
      </AppFrame>
      {/* {createUniversalPortal(<NavCookieBanner />, root)} */}
    </>
  );
}



function SectionHeader(props) {
  const { collapsed = false, title, ...rest } = props;

  return (
    <div
      style={{
        margin: `${core.layout.spacingSmall} 0 0`,
        opacity: collapsed ? 0 : 1,
        padding: `0 ${core.layout.spacingLarge}`,
      }}
      {...rest}
    >
      <Text.Heading size={Text.Heading.sizes.smallCaps}>
        <h2 style={{ margin: 0 }}>{title}</h2>
      </Text.Heading>
    </div>
  );
}

function CourseBranding(props) {
  return <NavBrand {...props} logo={<CourseLogo />} wordmark="SKILLS" />;
}

function CourseLogo() {
  return (
    <svg aria-hidden viewBox="0 0 32 32">
      <defs>
        <linearGradient
          id="skills-gradient"
          x1="45.6377"
          y1="47.4727"
          x2="-32.2436"
          y2="-35.2537"
          gradientUnits="userSpaceOnUse"
        >
          <stop offset="0.03" stopColor="#F05A28" />
          <stop offset="0.93" stopColor="#EB008B" />
        </linearGradient>
      </defs>
      <path
        d="M0 0V32H32V0H0ZM9.4053 12.7438L15.088 16L9.4053 19.287V12.7438ZM9.4053 24.8503V21.6468L19.1842 16L9.4053 10.3532V7.17166L24.6955 16L9.4053 24.8503Z"
        fill="url(#skills-gradient)"
      />
    </svg>
  );
}



function SkillsTopNav(props) {
  const { children, onMobileMenuClick, items = [] } = props;

  return (
    <div>
      <NavBar
        brand={<CourseBranding />}
        items={
          <Fragment>
            {items.map((item) => {
              return (
                <div
                  key={item.id}
                  style={{
                    marginRight: core.layout.spacingXXSmall,
                    display: "inline-block",
                  }}
                >
                  <NavItem icon={item.icon}>{item.title}</NavItem>
                </div>
              );
            })}

            {children}
          </Fragment>
        }
        onMobileMenuClick={onMobileMenuClick}
        user={<NavUser name="Jake" planName="Accenture" />}
      />
    </div>
  );
}

const MAIN_NAV_ITEMS = [
  {
    href: "#",
    icon: <Icon.HomeIcon />,
    id: "nav-home",
    title: "Home",
  },
  {
    href: "#",
    icon: <Icon.BrowseIcon />,
    id: "nav-browse",
    title: "Browse",
  },
];

const SIDE_NAV_SECTIONS = [
  {
    header: {
      href: "#",
      icon: <Icon.PlaceholderIcon />,
      id: "admin-tools",
      title: "Admin Tools",
    },
    items: [
      {
        href: "#",
        icon: <Icon.PlaceholderIcon />,
        id: "admin-tools__dashboard",
        title: "Dashboard",
      },
      {
        href: "#",
        icon: <Icon.PlaceholderIcon />,
        id: "admin-tools__account",
        title: "Account",
      },
      {
        href: "#",
        icon: <Icon.PlaceholderIcon />,
        id: "admin-tools__people",
        title: "People",
      },
      {
        href: "#",
        icon: <Icon.PlaceholderIcon />,
        id: "admin-tools__priorities",
        title: "Priorities",
      },
      {
        href: "#",
        icon: <Icon.PlaceholderIcon />,
        id: "admin-tools__analytics",
        title: "Analytics",
      },
      {
        href: "#",
        icon: <Icon.PlaceholderIcon />,
        id: "admin-tools__logs",
        title: "Logs",
      },
    ],
  },
  {
    header: {
      href: "#",
      icon: <Icon.PlaceholderIcon />,
      id: "content-tools",
      title: "Content Tools",
    },
    items: [
      {
        href: "#",
        icon: <Icon.PlaceholderIcon />,
        id: "home-tools__home",
        title: "Home",
      },
      {
        href: "#",
        icon: <Icon.PlaceholderIcon />,
        id: "admin-tools__dashboard",
        title: "Dashboard",
      },
    ],
  },
];

function useData() {
  const description =
    "An optional description to provide more detail about this step.";
  const { statuses } = Steps;

  const [steps, setSteps] = React.useState([
    { description, title: "Select type", status: statuses.completed },
    { description, title: "Select template", status: statuses.current },
    { description, title: "Add details", status: statuses.incomplete },
    { description, title: "Select goals", status: statuses.incomplete },
    { description, title: "Review", status: statuses.incomplete },
  ]);

  const select = React.useCallback(
    (nextStep) => {
      const curIndex = steps.indexOf(nextStep);
      if (curIndex < 0) return;

      setSteps((prev) =>
        prev.map((step, index) => ({
          ...step,
          status:
            index === curIndex
              ? "current"
              : index < curIndex
              ? "completed"
              : "incomplete",
        }))
      );
    },
    [steps]
  );

  return { steps, select };
}
