import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import { head } from "lodash";
import cookie from "react-cookies";
import internal from "stream";

export interface Login {
  email: string;
  password: string;
  csrfmiddlewaretoken:string;
}

export interface Signup {
  email: string;
  username: string;
  password1: string;
  password2: string;
  csrfmiddlewaretoken:string;
}

export interface Token {
  key: string;
}

export interface User {
  pk:number;
  email: string;
  username:string;
  first_name:string;
  last_name:string;

}

export interface Logout {

  csrfmiddlewaretoken:string;
}

export const authApi = createApi({
  baseQuery: fetchBaseQuery({
    baseUrl: "/",
    prepareHeaders: (headers) => {
      const csrfToken = cookie.load("csrftoken");
      const token = localStorage.getItem('token')
      if (csrfToken) {
        headers.set("X-CSRFToken", csrfToken);

      }
      if(token) {
        headers.set('Authorization Token', token)
      }

      headers.set("Content-Type", "application/json");

      return headers;
    },
  }),
  tagTypes: ["Login", "Signup", "Logout", "User", "Token"],
  endpoints: (build) => ({
    login: build.mutation<Token, Partial<Login>>({
      query: (body) => ({
        url: "rest-auth/login/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),
    signup: build.mutation<Signup, Partial<Signup>>({
      query: (body) => ({
        url: "rest-auth/registration/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),
    logout: build.mutation<Logout, Partial<Logout>>({
      query: (body) => ({
        url: "rest-auth/logout/",
        method: "POST",
        body,
        responseHandler: (response) => response.json(),
      }),
    }),
    getCurrentUser: build.query<User, {}>({
      query: () => ({
        url: "rest-auth/user/",
        method: "GET",

        responseHandler: (response) => response.json(),
      }),
    }),
  }),
});

export const { useLoginMutation, useSignupMutation, useLogoutMutation, useGetCurrentUserQuery } = authApi;
