import React from 'react';

import styles from './styles.module.scss';

export type TrackDetailProps = {
  trackNumber: number;
  title: string;
  short_description?: string;
};

export const TrackDetail: React.FC<TrackDetailProps> = ({
  trackNumber,
  title,
  short_description,
}) => {
  return (
    <div
      className={styles.trackDetailContainer}
      data-testid={`path-marketing-syllabus-track-${trackNumber}`}
    >
      <div className={styles.trackDetailNumber}>{trackNumber}</div>
      <div className={styles.trackDetailText}>
        <h3 className={styles.trackDetailTitle}>{title}</h3>
        <p className={styles.trackDetailDescription}>{short_description}</p>
      </div>
    </div>
  );
};
