import { theme } from '@codecademy/gamut-styles';
var variables = {
  borderColor: theme.colors['gray-200'],
  itemBackground: theme.colors.white,
  itemBackgroundHover: theme.colors['gray-200'],
  borderRadius: 2,
  transitionTime: '0.15s',
  formPadding: '0.8rem'
};
export { variables };
