import { AppBar, AppBarSection } from '@codecademy/gamut';
import { platformColors, pxRem } from '@codecademy/gamut-styles';
import styled from '@emotion/styled';
import React, { ReactNode } from 'react';
import { useSelector } from 'react-redux';

import { withErrorBoundary } from 'components/errors/withErrorBoundary';
import {
 HomeLogo, MenuTabs
} from './HeaderTabs/MenuTabs';
import { LEHeaderTab } from './HeaderTabs/utils';
export type HeaderProps = {
  adminTab?: ReactNode;
  IDEtabs?: ReactNode[];
  hideHelpMenu?: boolean;
  showTools?: boolean;
  statusDisplay?: ReactNode;
  walkthroughVideoUrl?: string;
  allowNavigation?: boolean;
  contentItemType?: string;
  kanbanExists?: boolean;
};

const Header = styled.header`
  position: relative;
  height: ${pxRem(64)};
  z-index: 14;
  background-color: ${platformColors.purple[900]};
`;

export const LEAppBarSection = styled(AppBarSection)`
  ${LEHeaderTab}:first-of-type {
    margin-left: 0;
  }
  ${LEHeaderTab}:last-of-type {
    margin-right: 0;
  }
`;

const LEHeader: React.FC<HeaderProps> = ({
  adminTab,
  IDEtabs,
  hideHelpMenu,
  showTools,
  statusDisplay,
  walkthroughVideoUrl,
  allowNavigation,
}) => {




  return (
    <Header>
      <AppBar wide>
        <LEAppBarSection key="header-app-bar-section-left" position="left">
          <HomeLogo />

        </LEAppBarSection>
        <LEAppBarSection key="header-app-bar-section-right" position="right">


          {/* { <IDETabs IDEtabs={IDEtabs} />} */}
          {/* <ProfileTab /> */}
          {/* { <AdTab />} */}
          {/* {hasGodMode && <AdminTab adminTab={adminTab} />} */}
        </LEAppBarSection>
      </AppBar>
    </Header>
  );
};

export default withErrorBoundary(LEHeader);
