import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import { head } from "lodash";
import cookie from "react-cookies";
import { RootState } from "store";


export interface TitleDescription {
    id: number;
    title: string;
    description: string
}

export interface HeaderDescription {
    id: number;
    heading: string;
    description: string
}

export interface Pricing {
  amount: string;
  currency: string;
  course: number;
}


export interface Review {
  id: number;
  state: string
}


export interface Course {
    id: string;
    title: string;
    description: string;
    url: string;
    tags: string[];
}

export interface Experience {
  name: string;
  id: number;
  course: number;
}

export interface Goal {
  name: string;
  id: number;
  course: number;
}

export interface Requirement {
  name: string;
  id: number;
  course: number;
}

export interface SectionRequest {
  title: string;
  id: number;
  course: number;
  description?: string;
  position: string;
  neighbor: number;
}


export interface ProjectEntityResponse {
    id: string;
    title: string;
    lead:Boolean;
    description:string;
    slug:string;
    goal:string;
    hero:string;
    category: number;
    thumbnail_url:string;
    experience:string;
    courses: Course[];
    completion_time:string;
    related: ProjectEntityResponse[]
    header:HeaderDescription,
    header_primary_color:string;
    header_secondary_color:string;
    video_headline:string;
    difficulty:string;
    progress:string;
    tags:string[];
    price:Number;
    outcomes: TitleDescription[];
    included: TitleDescription[];
    syllabuses: TitleDescription[];
}

export const projectApi = createApi({
  reducerPath: 'projectApi',
  baseQuery: fetchBaseQuery({
    baseUrl: "/",
    prepareHeaders: (headers, { getState }) => {
      const csrfToken = cookie.load("csrftoken");

      if (csrfToken) {
        headers.set("X-CSRFToken", csrfToken);
      }
      const token =
        (getState() as RootState).auth.token || localStorage.getItem("token");
      if (token) {
        headers.set("Authorization", `Token ${token}`);
      }

      headers.set("Content-Type", "application/json");

      return headers;
    },
  }),

  tagTypes: ['ProjectEntityResponse'],
  endpoints: build => ({
      fetchProjects: build.query<ProjectEntityResponse[], void>({
          query: () => ({
              url: 'api/projects/',
              method: "GET",
              responseHandler: (response) => response.json()
          }),
      }),
      createProject: build.mutation<ProjectEntityResponse, Partial<ProjectEntityResponse>>({
        query: (body) => ({
          url: "/projects/create/",
          method: "POST",
          body,
          responseHandler: (response) => response.json(),
        }),
      }),

      updateProject: build.mutation<ProjectEntityResponse, Partial<ProjectEntityResponse>>({
        query: (data) => {
          const { id, ...body} = data
          return {
            url: `/projects/${id}/edit/`,
            method: "PUT",
            body,
            responseHandler: (response) => response.json(),
          };
        },
      }),

      createProjectGoal: build.mutation<Goal[], Partial<Goal>>({
        query: (body) => ({
          url: "/projects/goal/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createPricing: build.mutation<Pricing[], Partial<Pricing>>({
        query: (body) => ({
          url: "/projects/pricing/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createExperience: build.mutation<Experience[], Partial<Experience>>({
        query: (body) => ({
          url: "/projects/experience/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createSyllabus: build.mutation<TitleDescription[], Partial<TitleDescription>>({
        query: (body) => ({
          url: "/projects/syllabus/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createIncluded: build.mutation<TitleDescription[], Partial<TitleDescription>>({
        query: (body) => ({
          url: "/projects/included/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),


      createLearningOutcome: build.mutation<TitleDescription[], Partial<TitleDescription>>({
        query: (body) => ({
          url: "/projects/outcome/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),


      editSyllabus: build.mutation<TitleDescription[], Partial<TitleDescription>>({
        query: (id, ...body) => ({
          url: "/projects/syllabus/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      editIncluded: build.mutation<TitleDescription[], Partial<TitleDescription>>({
        query: (id, ...body) => ({
          url: "/projects/included/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),


      editLearningOutcome: build.mutation<TitleDescription[], Partial<TitleDescription>>({
        query: (id, ...body) => ({
          url: "/projects/outcome/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createProjectRequirement: build.mutation<Requirement[], Partial<Requirement>>({
        query: (body) => ({
          url: "/projects/requirement/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),


      submitReview: build.mutation<Review, Partial<Review>>({
        query: (data) => {
         const { id, ...body } = data
         return  {url: `/projects/${id}/review/`,
          body,
          method: "PUT",
          responseHandler: (response) => response.json(),
        }
        }
      }),

      fetchExperience: build.query<Experience[], number>({
        query: (pk) => ({
          url: `/projects/${pk}/experience`,
          method: "GET",
          responseHandler: (response) => response.json(),
        }),
      }),

      fetchOutcomes: build.query<TitleDescription[], number>({
        query: (pk) => ({
          url: `/projects/${pk}/outcomes`,
          method: "GET",
          responseHandler: (response) => response.json(),
        }),
      }),

      fetchIncluded: build.query<Requirement[], number>({
        query: (pk) => ({
          url: `/projects/${pk}/requirements`,
          method: "GET",
          responseHandler: (response) => response.json(),
        }),
      }),
      deleteExperience: build.mutation<Experience, Partial<number>>({
        query: (id) => {
          return {
            url: `/projects/experience/${id}/`,
            method: "DELETE",
          };
        },
      }),

      deleteGoal: build.mutation<Goal, Partial<number>>({
        query: (id) => {
          return {
            url: `/projects/goals/${id}/`,
            method: "DELETE",
          };
        },
      }),

      deleteRequirement: build.mutation<Requirement, Partial<number>>({
        query: (id) => {
          return {
            url: `/projects/requirements/${id}/`,
            method: "DELETE",
          };
        },
      }),

  })
});

export const { useFetchProjectsQuery } = projectApi
