import classNames from "classnames";
import paypal from 'static/images/brand/paypal.svg'
import payoneer from 'static/images/brand/payoneer.svg'
export const PayoutMethod = ({ active }) => {
  return (
    <form
      className={classNames({
        hidden: !active,
      })}
    >

      <div id="premium-instructor-item-wrapper" className="item-wrapper">
        {" "}
        <div className="item-section">
          {" "}
          <div className="list-group-item list-group-item-info">
            {" "}
            <div className="df">
              {" "}
              <i className="ud-icon-info mr10" />{" "}
              <div>
                Choose your payout method below{" "}
                <a
                  href="https://support.udemy.com/hc/articles/229604948-Become-a-Premium-Instructor-and-Charge-for-Your-Course"
                  target="_blank"
                  rel=" noopener noreferrer"
                  pa-marked={1}
                >
                  (learn more)
                </a>
                . <br />
                <br /> Connecting to a new payout method may take a few days.
                You won't receive payments to the new linked account until its
                status is approved.
              </div>{" "}
            </div>{" "}
          </div>{" "}
        </div>{" "}
        <div className="item-section">
          {" "}
          <div className="list-group-item">
            {" "}
            <div className="fx-c fx-dc-xs">
              {" "}
              <div className="fx">
                {" "}
                <div className="flex-container">
                  {" "}
                  <div className="ach-heading">Direct Deposit (ACH) </div>{" "}
                  <div className="green-badge uppercase">Recommended</div>{" "}
                </div>{" "}
                <span className="item-section__status initial">
                  Avoid fees by using Direct Deposit
                </span>{" "}
              </div>{" "}
              <div className="fx-dc-xs">
                {" "}
                <button
                  type="button"
                  className="item-section__button btn btn-default btn-sm ud-component--user-manage--jpmorgan-manage"
                  pa-marked={1}
                >
                  Connect
                </button>{" "}
              </div>{" "}
            </div>{" "}
            <div className="df fx-dc-xs">

            </div>{" "}
          </div>{" "}
        </div>{" "}
        <button className="btn btn-link collapsible-button" pa-marked={1}>
          {" "}
          <span>Hide additional payout methods</span>{" "}
          <i className="cfi cfi-chevron-up" />{" "}
        </button>{" "}
        <div className="collapsible-area" style={{ display: "block" }}>
          {" "}
          <div className="item-section">
            {" "}
            <div className="list-group-item">
              {" "}
              <div className="df fx-dc-xs">
                {" "}
                <div className="fx">
                  {" "}
                  <img
                    src={paypal}
                    alt="PayPal"
                    width={124}
                    height={33}
                    className="item-section__paypal-logo"
                  />{" "}
                </div>{" "}
                <div className="fx-dc-xs">
                  {" "}
                  <span className="item-section__status initial">
                    Not Connected
                  </span>{" "}
                  <span
                    className="item-section__button ud-app-loader ud-component--paypal-login--app ud-app-loaded"

                  >
                    <span id="lippButton">
                      <a
                        id="LIwPP70234390"
                        className="LIwPP_V2 nuetral_V2 pillClass"
                        text="Log in with PayPal"
                        href="javascript:;"
                        type="button"
                        pa-marked={1}
                      >
                        <svg
                          className="PPTM"
                          xmlns="http://www.w3.org/2000/svg"
                          version="1.1"
                          width="25px"
                          height="25px"
                          viewBox="0 0 18 18"
                          focusable="false"
                        >
                          <path
                            id="Fill-8"
                            className="PPTM-btm"
                            fill="#009cde"
                            d="M13.6023044,4.67342223 C13.8196336,3.28812419 13.6008532,2.34542498 12.8514484,1.49160831 C12.0263956,0.551629995 10.5359309,0.148937623 8.62894883,0.148937623 L3.09322219,0.148937623 C2.70355347,0.148937623 2.37175455,0.43245482 2.3109822,0.817370681 L0.00598653054,15.4327457 C-0.0395473836,15.7211605 0.183405526,15.9818221 0.475294243,15.9818221 L3.89269613,15.9818221 L3.65686311,17.4774071 C3.61713431,17.7297247 3.81196867,17.957917 4.06739398,17.957917 L6.94782221,17.957917 C7.28887304,17.957917 7.57912907,17.7097715 7.63228221,17.3731061 L7.66058217,17.226722 L8.20317953,13.7860604 L8.23819166,13.5959605 C8.29134479,13.2591138 8.58160082,13.0111496 8.92247024,13.0111496 L9.35331903,13.0111496 C12.1439493,13.0111496 14.329033,11.8774436 14.9674148,8.59894727 C15.2342689,7.22906764 15.0963973,6.08556642 14.3908938,5.28163282 C14.1773742,5.03856625 13.9119713,4.83740146 13.6023044,4.67342223"
                          />
                          <path
                            id="Fill-9"
