import { createAsyncThunk, createSlice, PayloadAction 
} from "@reduxjs/toolkit";
import { v1 as uuid } from "uuid";
import { StringChain, stubFalse, without } from 'lodash';
import { AppThunk, RootState } from "store";
import { clamp } from "portal/scenes/CoursePlayer/utilities/clamp";
export interface PlayerState { 
    playing: boolean,
    playbackSpeed: number,
    activeMenu: string,
    volumeSliderActive: boolean
    volume: number,
    muted: boolean,

}



export const initialState: PlayerState = {
    playing: false,
    playbackSpeed: 1.0,
    activeMenu: null,
    volumeSliderActive: false,
    volume: 0.5,
    muted: false
}
export const playerSlice = createSlice({
    name: 'player',
    initialState,
    reducers: {
        play: (state, action: PayloadAction<void>) => {

            state.playing = true 
        },

        pause: (state, action: PayloadAction<void>) => {

            state.playing = false 
        },
        setPlaybackSpeed: (state, action: PayloadAction<number>) =>{
            state.playbackSpeed = action.payload
        },

        setActiveMenu: (state, action: PayloadAction<string>) =>{
            state.activeMenu = action.payload
        },
        setVolumeSliderActive: (state, action: PayloadAction<boolean>) =>{
            state.volumeSliderActive = action.payload
        },
        setVolume: (state, action: PayloadAction<number>) => {
            const clamped = clamp(action.payload, 0, 1)
            const muted = clamped === 0
            state.volume = clamped,
            state.muted = muted


        }


    }
});

export const selectPlayer = (state: RootState) => state.player



export const  { play, pause, setPlaybackSpeed, setActiveMenu, setVolumeSliderActive, setVolume } = playerSlice.actions



export default playerSlice.reducer;
