import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { UserSubmitKey } from "components/Forms/RegistrationForm/dist/types";
import type { User } from "services/auth";
import type { RootState } from "store";
import {coursesApi} from 'services/courses'
import { nullLiteralTypeAnnotation } from "@babel/types";

type CourseState = {
  title: string | null;
  description: string | null;
  category: string | null
};
const initialState = { title: null, description: null, category: null };
const courseSlice = createSlice({
  name: "course",
  initialState: initialState as CourseState,
  reducers: {

  },
  extraReducers: (builder) => {
    builder
      .addMatcher(
        coursesApi.endpoints.createCourse.matchFulfilled,
        (state, { payload }) => {
          state.title = payload.title;
          state.description = payload.description;
          state.category = payload.category

        }
      );
  },
});



export default courseSlice.reducer;

export const selectCourse = (state: RootState) => state.auth.user;
