import React, { useEffect, useState } from "react";
import { Lecture } from "services/courses";
import styles from "./style.module.css";

type LectureProps = {
  lecture: Lecture
}
export const LectureItem: React.FC<LectureProps> = ({ lecture}) => {

  const [seconds, setSeconds ] = useState(0)
  const [minutes, setMinutes] = useState(0)

  useEffect(() => {
   const minutes = Math.floor(lecture.duration / 60)
   const seconds = lecture.duration - minutes * 60
   setMinutes(minutes)
   setSeconds(s)
  }, [lecture])
  return (
    <button className={styles.lectureItemContainer}>
      <div className={styles.iconContainer}>
        <div className={styles.iconPosition}>
          <svg
            aria-label="selected"
            className={styles.svgIcon}
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 24 24"
          >
            <path d="M6.23 20.7L19.77 12 6.23 3.3" />
          </svg>
        </div>
      </div>
      <div className={styles.lectureItemDisplay}>
        <h3 className={styles.lectureItemH3}>
          <div className={styles.textCenter}>
            <span title="Introduction">{lecture.title}</span>
          </div>
        </h3>
        <div className={styles.lectureDuration}>1m 58s</div>
      </div>
    </button>
  );
};
