import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import { head } from "lodash";
import cookie from "react-cookies";
import { RootState } from "store";


export interface TitleDescription {
    id: number;
    title: string;
    description: string
}

export interface HeaderDescription {
    id: number;
    heading: string;
    description: string
}

export interface Course {
    id: string;
    title: string;
    description: string;
    url: string;
    tags: string[];
}

export interface Experience {
  name: string;
  id: number;
  course: number;
}

export interface Goal {
  name: string;
  id: number;
  course: number;
}

export interface Requirement {
  name: string;
  id: number;
  course: number;
}

export interface SectionRequest {
  title: string;
  id: number;
  course: number;
  description?: string;
  position: string;
  neighbor: number;
}


export interface ProjectEntityResponse {
    id: string;
    title: string;
    lead:Boolean;
    description:string;
    slug:string;
    goal:string;
    hero:string;
    category: number;
    thumbnail_url:string;
    experience:string;
    courses: Course[];
    completion_time:string;
    related: ProjectEntityResponse[]
    header:HeaderDescription,
    header_primary_color:string;
    header_secondary_color:string;
    video_headline:string;
    difficulty:string;
    progress:string;
    tags:string[];
    price:Number;
    outcomes: TitleDescription[];
    included: TitleDescription[];
    syllabuses: TitleDescription[];
}

export const projectApi = createApi({
  reducerPath: 'projectApi',
  baseQuery: fetchBaseQuery({
    baseUrl: "/",
    prepareHeaders: (headers, { getState }) => {
      const csrfToken = cookie.load("csrftoken");

      if (csrfToken) {
        headers.set("X-CSRFToken", csrfToken);
      }
      const token =
        (getState() as RootState).auth.token || localStorage.getItem("token");
      if (token) {
        headers.set("Authorization", `Token ${token}`);
      }

      headers.set("Content-Type", "application/json");

      return headers;
    },
  }),

  tagTypes: ['ProjectEntityResponse'],
  endpoints: build => ({
      fetchProjects: build.query<ProjectEntityResponse[], void>({
          query: () => ({
              url: 'api/projects/',
              method: "GET",
              responseHandler: (response) => response.json()
          }),
      }),
      createProject: build.mutation<ProjectEntityResponse, Partial<ProjectEntityResponse>>({
        query: (body) => ({
          url: "/projects/create/",
          method: "POST",
          body,
          responseHandler: (response) => response.json(),
        }),
      }),

      updateCourse: build.mutation<CourseResponse, Partial<CourseRequest>>({
        query: (data) => {
          const { id, ...body} = data
          return {
            url: `/courses/${id}/edit/`,
            method: "PUT",
            body,
            responseHandler: (response) => response.json(),
          };
        },
      }),

      uploadLectureVideo: build.mutation<MediaResponse, Partial<MediaRequest>>({
        query: (body) => ({
          url: "/courses/lecture/video_upload/",
          method: "POST",
          body,
          responseHandler: (response) => response.json(),
        }),
      }),
      createSection: build.mutation<Section[], Partial<SectionRequest>>({
        query: (body) => ({
          url: "/courses/section/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createGoal: build.mutation<Goal[], Partial<Goal>>({
        query: (body) => ({
          url: "/courses/goal/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createPricing: build.mutation<Pricing[], Partial<Pricing>>({
        query: (body) => ({
          url: "/courses/pricing/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createExperience: build.mutation<Experience[], Partial<Experience>>({
        query: (body) => ({
          url: "/courses/experience/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      createRequirement: build.mutation<Requirement[], Partial<Requirement>>({
        query: (body) => ({
          url: "/courses/requirement/create/",
          body,
          method: "POST",
          responseHandler: (response) => response.json(),
        }),
      }),

      editRequirement: build.mutation<Requirement, Partial<Requirement>>({
        query: (data) => {
         const { id, ...body } = data
         return  {url: `/courses/requirements/${id}/`,
          body,
          method: "PUT",
          responseHandler: (response) => response.json(),
        }
        },
      }),

      editExperience: build.mutation<Experience, Partial<Experience>>({
        query: (data) => {
         const { id, ...body } = data
         return  {url: `/courses/experience/${id}/`,
          body,
          method: "PUT",
          responseHandler: (response) => response.json(),
        }
        },
      }),

      editGoal: build.mutation<Goal, Partial<Goal>>({
        query: (data) => {
         const { id, ...body } = data
         return  {url: `/courses/goals/${id}/`,
          body,
          method: "PUT",
          responseHandler: (response) => response.json(),
        }
        },
      }),

      submitReview: build.mutation<Review, Partial<Review>>({
        query: (data) => {
         const { id, ...body } = data
         return  {url: `/courses/${id}/review/`,
          body,
          method: "PUT",
          responseHandler: (response) => response.json(),
        }
        }
      }),

      fetchExperience: build.query<Experience[], number>({
        query: (pk) => ({
          url: `/courses/${pk}/experience`,
          method: "GET",
          responseHandler: (response) => response.json(),
        }),
      }),

      fetchGoals: build.query<Goal[], number>({
        query: (pk) => ({
          url: `/courses/${pk}/goals`,
          method: "GET",
          responseHandler: (response) => response.json(),
        }),
      }),

      fetchRequirements: build.query<Requirement[], number>({
        query: (pk) => ({
          url: `/courses/${pk}/requirements`,
          method: "GET",
          responseHandler: (response) => response.json(),
        }),
      }),
      deleteExperience: build.mutation<Experience, Partial<number>>({
        query: (id) => {
          return {
            url: `/courses/experience/${id}/`,
            method: "DELETE",
          };
        },
      }),

      deleteGoal: build.mutation<Goal, Partial<number>>({
        query: (id) => {
          return {
            url: `/courses/goals/${id}/`,
            method: "DELETE",
          };
        },
      }),

      deleteRequirement: build.mutation<Requirement, Partial<number>>({
        query: (id) => {
          return {
            url: `/courses/requirements/${id}/`,
            method: "DELETE",
          };
        },
      }),

  })
});

export const { useFetchProjectsQuery } = projectApi
