  onClickNext?: () => void;
  buttonText?: string;
  nextDisabled?: boolean;
  customNavigation?: JSX.Element | React.ReactNode;
  customProgressIndicator?: JSX.Element;
};

export const SimpleFooter: React.FC<SimpleFooterProps> = ({
  onClickNext,
  buttonText,
  nextDisabled,
  customNavigation,
  customProgressIndicator,
}) => {
  // const defaultNavigation = (
