  return (
    <Footer className={styles.footer}>
      <FooterSection position="left" />
      <FooterSection position="center">{customProgressIndicator}</FooterSection>
      <FooterSection position="right">

      </FooterSection>
    </Footer>
  );
};

SimpleFooter.defaultProps = {
  buttonText: 'Next',
};
