import { useState } from "react";
import { LectureCreate } from "./Lecture/lectureCreate";
import { Resource } from "../Resource";
import { SectionCreate } from './SectionCreate';
import { Preview } from "./Preview";
import { LectureEdit } from "./Lecture/LectureEdit";
import { UploadVideo } from "./Lecture/UploadVideo";

import { Resources } from "./Resources";
import { LectureDescription } from "./LectureDescription";
import { Downloadables } from "./Downloadables";
export const Lecture = () => {

  const [toggleDescription, setToggleDescription ] = useState(false)
  const [toggleEdit, setToggleEdit] = useState(false);
  const [toggleAdd, setToggleAdd] = useState(false)
  const [lectureEdit, setLectureEdit] = useState(false)
  const [toggleResource, setToggleResource] = useState(false)

  return (
    <div>
      <div className="pt20 pb30">
        <div className="add-item-forms--inline-menu--1OTdc">
          <button
            data-purpose="add-lecture-btn"
            aria-label="Add Lecture"
            type="button"
            className="ellipsis btn btn-tertiary btn-block"
          >
            <span className="a3 udi udi-plus-square" /> Lecture
          </button>
          <button
            data-purpose="add-quiz-btn"
            aria-label="Add Quiz"
            type="button"
            className="ellipsis btn btn-tertiary btn-block"
          >
            <span className="a3 udi udi-plus-square" /> Quiz
          </button>
          <button
            data-purpose="add-coding-exercise-btn"
            aria-label="Add Coding Exercise"
            type="button"
            className="ellipsis btn btn-tertiary btn-block"
          >
            <span className="a3 udi udi-plus-square" /> Coding Exercise
          </button>
          <div className="disabled-trigger">
            <button
              disabled
              data-purpose="add-practice-test-btn"
              aria-label="Add Practice Test"
              type="button"
              className="ellipsis btn btn-tertiary btn-block"
            >
              <span className="a3 udi udi-plus-square" /> Practice Test
            </button>
          </div>
          <button
            data-purpose="add-assignment-btn"
            aria-label="Add Assignment"
            type="button"
            className="ellipsis btn btn-tertiary btn-block"
          >
            <span className="a3 udi udi-plus-square" /> Assignment
          </button>
        </div>
      </div>
    </div>
  );
};
