import sha1 from 'tiny-hashes/sha1';
import { colors, fallbackPixel } from '../vars/index';
export var getInitials = function (fullname) {
    if (!fullname || !fullname.length)
        return ':)';
    return fullname[0].toUpperCase();
};
export var getColorByName = function (name) {
    var stableRandNumber = parseInt(sha1(name).replace(/[^0-9]/g, '')[0], 10);
    return colors[stableRandNumber] || colors[0];
};
export var transformSrc = function (src) {
    if (src === void 0) { src = ''; }
    var hasGravatar = /gravatar/.exec(src);
    if (!hasGravatar)
        return src;
    var sep = /\?/.exec(src) ? '&' : '?';
    var fallback = encodeURIComponent(fallbackPixel);
    return "" + src + sep + "d=" + fallback;
};
