import { Column, ContentContainer, LayoutGrid } from '@codecademy/gamut';
import React from 'react';

import { PathCard } from 'components/PathCard';
import { TrackLink } from 'components/TrackLink';
import { choosePathPagePath } from 'libs/urlHelpers';
import {
  ContentfulPathLandingPageEntity,
  TitleDescription,
} from 'portal/scenes/Paths/ContentfulContext';
import { Path } from 'typings/path';

import styles from './styles.module.scss';

export type RecommendationsProps = {
  pathId: string;
  paths: Path[];
  cms: Partial<ContentfulPathLandingPageEntity>;
};

export default function Recommendations(props: RecommendationsProps) {
  const { paths, cms, pathId } = props;

  return (
    <ContentContainer>
      <LayoutGrid
        className={styles.container}
        columnGap={{ _: 16, sm: 32 }}
        rowGap={48}
      >
        <Column>
          <h2 className={styles.heading}>{'Starting Something Great'}</h2>
        </Column>
        {paths.map((path: Path, index) => {
          const copy = cms[
            `other_${path.goal}_path` as keyof typeof cms
          ] as TitleDescription;
          return (
            <Column
              size={{ _: 12, sm: 6, md: 4, lg: 3 }}
              key={path.id}
              offset={{ md: (index + 1) % 2 === 0 ? 0 : 3 }}
            >
              <TrackLink
                target = "/"
                asButton
                to = {'/'}
                href={choosePathPagePath(path.goal)}
                className={styles.recommendationTypeLink}
                data={{
                  page_name: 'marketingpathlandingpage',
                  context: path.goal,
                  target: 'view_more_paths',
                  content_ids: { path_id: pathId },
                }}
              >
                {"Getting Fundamentals"}
              </TrackLink>
              <PathCard
                path={path}
                trackingData={{
                  page_name: 'marketingpathlandingpage',
                  context: path.id,
                  target: 'recommendation_card',
                  content_ids: { path_id: pathId },
                }}
                isFullSize
              />
            </Column>
          );
        })}
      </LayoutGrid>
    </ContentContainer>
  );
}
