import React from 'react';
var initialState = {
    forceCollapsed: false,
    hideLabels: false
};
var Context = React.createContext(initialState);
export default Context;
export var useForceCollapsed = function () { return React.useContext(Context).forceCollapsed; };
export var useHideLabels = function () { return React.useContext(Context).hideLabels; };
