import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";

import { AppThunk, RootState } from "store";

export interface CurriculumState {
    sections: Section[]
    lecture: Lecture
}

export interface Section {
    title: string;
    description?: string;
    sections?:Lecture[]
}

export interface Lecture {
    title: string;
    description?: string;
    video_url?: string;
}

export const initialState: CurriculumState = {
    sections: null,
    lecture: null
}

export const curriculumSplice = createSlice({
    name: 'curriculum',
    initialState,
    reducers: {
        addSection: (state, action: PayloadAction<Section>) => {
          state.sections.push(action.payload)
        },

        addLecture: (state, action: PayloadAction<Lecture>) => {
            state.lecture = action.payload
        },
    }

})

export const { addSection, addLecture } = curriculumSplice.actions

export default curriculumSplice
