import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query'
import { login } from 'components/gamut-overrides/GlobalHeader/GlobalHeaderItems'

export interface Login {
    email: string,
    password: string
}

export interface Signup {
    email: string,
    password: string,
    password2: string
}

export const authApi = createApi({
    baseQuery: fetchBaseQuery({ baseUrl: '/',
    prepareHeaders: (headers) => {
        return headers
    }
    }),
    tagTypes: ['Login'],
    endpoints: (build) => ({
       signin:  build.mutation<Login, Partial<Login>>({
           query: (body) => ({
               url: 'accounts/login',
               method: 'POST',
               body
           })
       }),
    })
});
