import { useState } from "react";
import classNames from "classnames";
import "./styles.scss";
import { Lecture } from "./Lecture";
import { LectureCreate } from "./Lecture/lectureCreate";
import { SectionCreate } from './SectionCreate';
import { SectionEdit } from "./SectionEdit";


export const Section = () => {
  const [lecture, setLecture] = useState('')
  const [section, setSection] = useState('')
  const [sectionEdit, setSectionEdit] = useState(false)
  const [lectures, setLectures] = useState([{
    title: 'Introduction',
    video_url: ''
  }])
  return (
    <>
      <li className="js-curriculum-item-draggable curriculum-list--curriculum-list__section--2SsIO curriculum-list--curriculum-list__section--inline--2E-oq">
        <div className="curriculum-list--item-wrap--1GkZz">
          <div className="curriculum-list--add-item-section-wrapper--bXWiB">
            <div className={classNames("curriculum-list--wrapper-section--3v7bx", {
                'curriculum-list--open--VjfTb': section == 'newSection1'
            })}>
              <button
                id={'newSection1'}
                aria-label="New curriculum item"
                onClick={(event) => {
                    setSection(event.target.id)
                }}
                type="button"
                className={classNames("curriculum-list--add-item-section--3JmsU add-item--add-item-toggler--1k-rh add-item--section--1_IVB btn btn-xs btn-link", {'add-item--open--2FcI6': section == 'newSection1'})}
              >
                <span className={classNames("add-item--icon--2FsmW cfi-small cfi cfi-close", {
                    'add-item--open--2FcI6': section == 'newSection1',
                    'add-item--rotate--WJ08z': section != 'newSection1'
                })} />
              </button>
            </div>
            {section == 'newSection1' && <SectionCreate className={'curriculum-list--over-section--1DyMX'} toggle={() => setSection('') }/>}
          </div>
          <div data-purpose="section-editor">
            {sectionEdit && <SectionEdit toggle={() => setSectionEdit(!sectionEdit)} />}
            <div className="fx-lt item-icon-button-trigger js-curriculum-item-handle item-bar--item-bar--1qWKU item-bar--item-bar--section--3aQ_4">
              <div className="fx">
                <div className="fx-lt fxwrap">
                  <div className="item-bar--item-bar__left--1nfEv">
                    <div
                      className="fx-lc item-bar--item-bar__title--3ww0E"
                      data-purpose="item-full-title"
                    >
                      <span
                        className="item-bar--item-bar__status--MZ70P"
                        data-purpose="item-object-index"
                      >
                        <span data-purpose="empty-status-icon">&nbsp;</span>
                        Section 1:
                      </span>
                      <span className="hidden-xxs ellipsis pl10">
                        <span
                          data-purpose="section-icon"
                          className="pr5 cfi cfi-file-text-o"
                        />
                        <span>GraphQL Fundamentals</span>
                      </span>
                      <button
                        onClick={() => {
                            setSectionEdit(!sectionEdit)
                        }}
                        data-purpose="section-edit-btn"
                        type="button"
                        className="item-icon-button--icon-button--1IKG6 btn btn-xs btn-quintinary"
                      >
                        <span className="cfi cfi-pencil" />
                        <span className="sr-only">Edit</span>
                      </button>
                      <button
                        data-purpose="section-delete-btn"
                        type="button"
                        className="item-icon-button--icon-button--1IKG6 btn btn-xs btn-quintinary"
                      >
                        <span className="cfi cfi-trash-o" />
                        <span className="sr-only">Delete</span>
                      </button>
                    </div>
                    <div className="visible-xxs ellipsis pb5">
                      <span
                        data-purpose="section-icon"
                        className="pr5 cfi cfi-file-text-o"
                      />
                      <span>GraphQL Fundamentals</span>
                    </div>
                  </div>
                </div>
              </div>
              <div className="item-bar--item-bar__right--1gA3p">
                <div
                  type="button"
                  className="item-bar--item-bar__handle--1DqDp item-icon-button--icon-button--1IKG6 btn btn-xs btn-quintinary"
                >
                  <span className="cfi cfi-bars" />
                  <span className="sr-only">Move</span>
                </div>
              </div>
            </div>
          </div>
          <div className="curriculum-list--add-item-wrapper--2CGL-">
            <div className={classNames("curriculum-list--wrapper--2Jw7f", {
                'curriculum-list--open--VjfTb': lecture == 'newLecture1'
            })}>
              <button
                id={'newLecture1'}
                onClick={(event) => {
                    setLecture(event.target.id)
                }}
                aria-label="New curriculum item"

                type="button"
                className={classNames("curriculum-list--add-item--1DK2G add-item--add-item-toggler--1k-rh add-item--lecture--2ZWW9 btn btn-xs btn-link", {
                    'add-item--open--2FcI6': lecture == 'newLecture1'
                })}
              >
                <span className={classNames("add-item--icon--2FsmW  cfi-small cfi cfi-close", {
                   'add-item--rotate--WJ08z': lecture != 'newLecture1',
                   'add-item--open--2FcI6': lecture == 'newLecture1'
                })} />
              </button>
            </div>
            <div>
               {lecture == "newLecture1" && <LectureCreate toggle={()=>
                   setLecture('')
               } />}
            </div>
          </div>
        </div>
      </li>
      {lectures &<li
        id="lecture27580746"
        className="js-curriculum-item-draggable curriculum-list--curriculum-list__section-item--1hfk_ curriculum-list--curriculum-list__section-item--inline--aFpJo"
      >
        <div className="curriculum-list--item-wrap--1GkZz">
          <Lecture />
          <div className="curriculum-list--add-item-wrapper--2CGL-">
            <div className={classNames("curriculum-list--wrapper--2Jw7f", {
                'curriculum-list--open--VjfTb': lecture == 'newLecture2'
            })}>
            <button
                id={'newLecture2'}
                onClick={(event) => {
                    setLecture(event.target.id)
                }}
                aria-label="New curriculum item"
                data-purpose="add-item-inline"
                type="button"
                className={classNames("curriculum-list--add-item--1DK2G add-item--add-item-toggler--1k-rh add-item--lecture--2ZWW9 btn btn-xs btn-link", {
                    'add-item--open--2FcI6': lecture == 'newLecture2'
                })}
              >
                <span className={classNames("add-item--icon--2FsmW  cfi-small cfi cfi-close", {
                   'add-item--rotate--WJ08z': lecture != 'newLecture2',
                   'add-item--open--2FcI6': lecture == 'newLecture2'
                })} />
              </button>
            </div>
          </div>
          <div>
          {lecture == "newLecture2" && <LectureCreate toggle={() => setLecture('')
          } />}
          </div>
          <div className="curriculum-list--add-item-section-wrapper--bXWiB">
            <div className={classNames("curriculum-list--wrapper-section--3v7bx curriculum-list--is-last-item--1Diog", {
                'curriculum-list--open--VjfTb': section == 'newSection2'
            })}>
              <button
                id={'newSection2'}
                onClick = {(event) => {
