                aria-label="New curriculum item"
                data-purpose="add-item-inline"
                type="button"
                className={classNames("curriculum-list--add-item-section--3JmsU add-item--add-item-toggler--1k-rh add-item--section--1_IVB btn btn-xs btn-link", {'add-item--open--2FcI6': section == 'newSection2'})}
              >
                <span className={classNames("add-item--icon--2FsmW cfi-small cfi cfi-close", {
                    'add-item--open--2FcI6': section == 'newSection2',
                    'add-item--rotate--WJ08z': section != 'newSection2'
                })} />
              </button>
            </div>
            {section == 'newSection2' && <SectionCreate className={'curriculum-list--under-section--zkvIv'} toggle={() => setSection('') }/>}
          </div>

        </div>
      </li>}
    </>
  );
