const AlertTemplate = ({ style, options, message, close }) => (
    <div style={style}>
      {options.type === 'info' && '!'}
      {options.type === 'success' && ':)'}
      {options.type === 'error' && ':('}
      {message}
      <button onClick={close}>X</button>
    </div>
  )


export const FeedbackMessage = ({
  text = "Your changes have been successfully saved",
}) => {
  return (
    <div
      className="feedbacks-bar--feedbacks-bar--RapCY"
      style={{ display: "block" }}
    >
      <div

        role="button"
        tabIndex={0}
        className="ui-feedback-close-btn feedbacks-bar--feedback-bar--3OWyr feedbacks-bar--feedback-type-info--1PAbl"
        style={{ maxHeight: "0px" }}
      >
        <span className="feedbacks-bar--close--2wAwj cfi cfi-close" />
        <div className="feedbacks-bar--feedback-container--3Dpkf">
          <p className="feedbacks-bar--message--3Na3Q">
            { text}
          </p>
        </div>
      </div>
    </div>
  );
};
